function openModel(e){var t=e.getAttribute("data-tittle"),o=e.getAttribute("data-synopsis"),e=e.getAttribute("data-img-src"),n=(console.log(e),document.getElementById("modal-title")),d=document.getElementById("modal-body"),l=document.getElementById("modal-image"),c=document.querySelector(".modal-overlay");n.textContent=t,d.textContent=o,l.src=e,l.alt=t,c.style.display="block"}function closeModel(){document.getElementById("model").style.display="none"}document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".open-modal-link").forEach(e=>{e.addEventListener("click",function(){openModel(this)})}),document.querySelector(".modal-overlay").addEventListener("click",closeModel)});